import { createRouter, createWebHistory } from 'vue-router';
import LoginComponent from '../components/LoginComponent.vue'; // 登录组件
import RegisterComponent from '../components/RegisterComponent.vue'; // 注册组件
import Main from '../components/MainComponent.vue'; // 主页组件(原HelloWorld)

const routes = [
    {
        path: '/login',
        name: 'Login',
        component: LoginComponent,
    },
    {
        path: '/register',
        name: 'Register',
        component: RegisterComponent,
    },
    {
        path: '/main',
        name: 'Main',
        component: MainComponent,
    },
];

const router = createRouter({
    history: createWebHistory(),
    routes,
});

export default router;
